CREATE DATABASE express;
USE express;
CREATE TABLE express_services(
	service_id int auto_increment primary key,
	service_name TEXT,
	service_description TEXT,
	service_name_image TEXT
);


CREATE TABLE services_menu(
	menu_id int auto_increment primary key,
	service_id TEXT,
	service_name TEXT,
	menu_name TEXT,
	menu_description TEXT,
	menu_image TEXT
);

CREATE TABLE express_slider(
	slider_id int auto_increment primary key,
	slider_name TEXT,
	slider_text TEXT,
	slider_link TEXT
);






